pragma solidity ^0.4.23;

import "../ProductionContract.sol";
import "../Upgradeability/OwnableUpgradeableImplementation/IOwnableUpgradeableImplementation.sol";

contract IProductionFactory is IOwnableUpgradeableImplementation {

	event LogOrderCreated(address manufacturer, address orderContract);
	event LogWithdraw(address receiver, uint amount);
	event LogBlacklisted(address bannedUser, bool added);

	function init(address inTokenContract) public;
	function tokenContract() public view returns(address);
	function setTokenContract(address inTokenContract) public;
	function productionRegistry() public view returns(address);
	function setProductionRegistry(address _productionRegistry) public;
	function isBlacklisted(address blacklistedAddress) public view returns(bool);
	function ordersHistory(address historyAddress, uint index) public view returns(uint, address, uint);
	function calculateServiceTax(uint _quantity, uint _pricePerUnit) public view returns(uint);
	function createContractForConsumer(address _consumer, address _manufacturer, uint _quantity, ProductionContract.PRODUCT_FORM _form, string _engravingText, uint _pricePerUnit, string _shipmentAddress, bytes32 _freightAgent) public payable returns(address);
	function createContract(address _manufacturer, uint _quantity, ProductionContract.PRODUCT_FORM _form, string _engravingText, uint _pricePerUnit, string _shipmentAddress, bytes32 _freightAgent) public payable returns(address);
	function addToOrdersHistory(address _consumer, address orderContract) internal;
	function getOrdersHistoryLength(address consumer) public view returns(uint);
	function blacklist(bool add, address user) private returns(bool);
	function addToBlacklist(address user) external returns(bool);
	function removeFromBlacklist(address user) external returns(bool);
	function withdraw() external;
	function withdrawTo(address to) external;
}
